import { StyleSheet, Text, Button, View, TextInput } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useState } from 'react';
import { styles } from '../assets/styles/Styles';
import Banner from '../components/Banner';
import { Controller, useForm } from 'react-hook-form';



function ProfileScreen({ route }) {

  const { control, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      id: '',
      fecha: '',
      saldo: '',
    }
  });


  return (
    <View style={styles.container}>
      <Banner img="bank.png" />
      <Text style={styles.texts}>Bienvenida: {route.params.user}</Text>
      <Text style={styles.texts}>Nº Cuenta:</Text>
      <TextInput style={styles.TextInput}> {Math.floor(Math.random() * (999999999 - 100000000 + 1) + 100000000)} </TextInput>

      <Text style={styles.texts}> Idetificación: </Text>
      <Controller
        control={control}
        rules={{
          required: true,        
        }}
        render={({ field: { onChange, onBlur, value, } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Administrador / Usuario"            
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='id'
      />
      {errors.rol?.type == "required" && <Text style={{ color: 'red' }}>El Rol es obligatorio</Text>}




    </View>
  );
}

export default ProfileScreen;
