import { StyleSheet, Text, Button, View, TextInput } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useState } from 'react';
import { styles } from '../assets/styles/Styles';
import Banner from '../components/Banner';
import { Controller, useForm } from 'react-hook-form';



function ProfileScreen({ route }) {

  const { control, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      id: '',
      titular: '',
      fecha: '',
      saldo: '',
    }
  });


  return (
    <View style={styles.container}>
      <Banner img="bank.png" />
      <Text style={styles.texts}>Bienvenida: {route.params.user}</Text>
      <Text style={styles.texts}>Nº Cuenta:</Text>
      <TextInput style={styles.TextInput}> {Math.floor(Math.random() * (999999999 - 100000000 + 1) + 100000000)} </TextInput>

      <Text style={styles.texts}> Idetificación: </Text>
      <Controller
        control={control}
        rules={{
          required: true,  
          pattern: /^[0-9]/,              
          minLength: 8,
          maxLength: 10,
          
        }}
        render={({ field: { onChange, onBlur, value, } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Identificación del titular"            
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='id'        
      />
      {errors.id?.type == "required" && <Text style={{ color: 'red' }}> La identificación es obligatoria </Text>}
      {errors.id?.type == "pattern" && <Text style={{ color: 'red' }}> La identificación solo depe bontener numeros </Text>}
      {errors.id?.type == "minLengthin" || "maxLength" && <Text style={{ color: 'red' }}>La identificación debe tener de 8 a 10 caracteres</Text>}

      <Text style={styles.texts}> Nombre del titular: </Text>
      <Controller
        control={control}
        rules={{
          required: true,  
          pattern: /^[A-Za-zÑñáéíóú]+$/i,                
        }}
        render={({ field: { onChange, onBlur, value, } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Nombre del titular"            
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='titular'        
      />
      {errors.titular?.type == "required" && <Text style={{ color: 'red' }}> El nombre del titular es obligatoria </Text>}
      {errors.titular?.type == "pattern" && <Text style={{ color: 'red' }}> El nombre del titular solo debe contener letras y/o espacios </Text>}
      

      <Text style={styles.texts}> Fehca: </Text>
      <Controller
        control={control}
        rules={{
          required: true,  
          pattern: /^([0][1-9]|[12][0-9]|3[01])(\/|-)([0][1-9]|[1][0-2])\2(\d{4})$/               
        }}
        render={({ field: { onChange, onBlur, value, } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Fecha de nacimiento dd/mm/aaaa"            
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='fecha'        
      />
      {errors.fehca?.type == "required" && <Text style={{ color: 'red' }}> La fecha es obligarotria </Text>}
      {errors.fecha?.type == "pattern" && <Text style={{ color: 'red' }}> Formato de fecha no valido debe ir en orden: dd/mm/aaaa </Text>}
      



      




    </View>
  );
}

export default ProfileScreen;
