import { StyleSheet, Text, Button, View, TextInput, TouchableOpacity, Picker } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useState } from 'react';
import { styles } from '../assets/styles/Styles';
import { Controller, useForm } from 'react-hook-form';

function UserScreen({ navigation }) {
  const [fullname, setFullUser] = useState('');
  const [email, setEmail] = useState('');
  const { control, hundleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      user: '',
      rol: '',
      password: '',
    }
  });
  // const validate = () => {
  //   if (email == "tv@gmail.com") {
  //     setEmail("");
  //     setFullUser("")
  //     navigation.navigate('Profile', { fullname: fullname })
  //   }
  // }
  const onSubmit = data => console.log(data);
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Controller
        control={control}
        rules={{
          required: true,
          pattern: /^[A-Za-zÑñáéíóú]+$/i,
        }}
        render={({ field: { onChange, onBlur, value } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="User"
            onChangeText={value => setFullUser(value)}
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='user'
      />
      {errors.user?.type == "required" && <Text style={{ color: 'red' }}>El nombre es obligatorio</Text>}
      {errors.user?.type == "pattern" && <Text style={{ color: 'red' }}>El usuario solo debe tener letras y/o espacios</Text>}

      <Controller
        control={control}
        rules={{
          required: true,
          pattern: /^[A-Za-zÑñáéíóú]+$/i,
        }}
        render={({ field: { onChange, onBlur, value } }) => (
          <Picker
          selected
          >

          </Picker>
        )}
        name='rol'
      />
      <TextInput
        style={styles.inputs}
        onChangeText={value => setEmail(value)}
        value={email}
      />
      <Button
        title="Perfil del Usuario"
        //onPress={() => navigation.navigate('Settings')}
        //onPress={validate}
        onPress={() => {
          if (email == "tv@gmail.com") {
            setEmail("");
            setFullUser("")
            navigation.navigate('Profile', { fullname: fullname })
          }
        }}

      />
    </View>
  );
}

export default UserScreen;
