import React from 'react';
import { StyleSheet, Text, Button, View, TextInput, TouchableOpacity, Picker } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { useState } from 'react';
import { styles } from '../assets/styles/Styles';
import { Controller, useForm } from 'react-hook-form';
import Banner from '../components/Banner';

let users = [
  { user: 'matiiutv', rol: 'admin', password: 'Elpapasito.123' },
  { user: 'mery', rol: 'admin', password: 'Elpapasito.123' }
];

function UserScreen({ navigation }) {
  const { control, handleSubmit, formState: { errors } } = useForm({
    defaultValues: {
      user: '',
      rol: '',
      password: '',
    }
  });

  const onSubmit = data => {
    let userSearch = users.find(user => user.user === data.user.toLowerCase() && user.rol === data.rol && user.password === data.password );
    console.log(data);
    if (userSearch != data.name) {
      return alert('Usuario no registrado');
    }
    if (userSearch != data.rol) {
      return alert(`El rol no es el designado para el usuario ${data.user}`);
    }
    if(userSearch != data.password) {
      return 
    }
    navigation.navigate('Cuenta', { user: data.user.toLowerCase() });
  };
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Banner img="bank.png" />
      <Text style={[styles.texts]}>Usuario:</Text>
      <Controller
        control={control}
        rules={{
          required: true,
          pattern: /^[A-Za-zÑñáéíóú]+$/i,
        }}

        render={({ field: { onChange, onBlur, value } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Ingrese su usuario"
            // onChangeText={value => setFullUser(value)}
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='user'
      />
      {errors.user?.type == "required" && <Text style={{ color: 'red' }}>El nombre es obligatorio</Text>}
      {errors.user?.type == "pattern" && <Text style={{ color: 'red' }}>El usuario solo debe tener letras y/o espacios</Text>}

      <Text style={styles.texts}> Rol: </Text>
      <Controller
        control={control}
        rules={{
          required: true,
          deps: ['administrador']
        }}
        render={({ field: { onChange, onBlur, value } }) => (
          <TextInput
            style={[styles.inputs]}
            placeholder="Administrador / Usuario"
            // onChangeText={value => SetRol(value)}
            onBlur={onBlur}
            value={value}
            onChange={onChange}
          />
        )}
        name='rol'
      />
      {errors.rol?.type == "required" && <Text style={{ color: 'red' }}>El Rol es obligatorio</Text>}
      {errors.rol?.type == "deps" && <Text style={{ color: 'red' }}>Rol no valido</Text>}

      <Text style={styles.texts}> Contraseña: </Text>
      <Controller
        control={control}
        rules={{
          required: true,
          pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[$@$!%*?&+.])[A-Za-z\d$@$!%*?&]{8,15}/,
        }}
        render={({ field: { onChange, onBlur, value } }) => (
          <TextInput
            style={styles.inputs}
            onBlur={onBlur}
            placeholder='Elija su contraseña'
            secureTextEntry={true}
            // onChangeText={value => setPassword(value)}
            onChange={onChange}
            value={value}
          />
        )}
        name='password'
      />
      {errors.password?.type == "required" && <Text style={{ color: 'red' }}>La contraseña es obigatorio</Text>}
      {errors.password?.type == "pattern" && <Text style={{ color: 'red' }}>La contraseña debe tener entro 8 y 15 caracteres, contener letras mayusculas, numeros y por lo menos un caracter especial</Text>}

      <TouchableOpacity
        style={styles.buttons}
        onPress={(handleSubmit(onSubmit))}
      >
        <Text style={styles.textbuttons}>Ingresar</Text>
      </TouchableOpacity>
    </View>
  );
}

export default UserScreen;
