import { StyleSheet, Text, View } from 'react-native';
import { styles, viewsChilds } from '../assets/styles/Styles';

const Banner = props => {
    return (
        <View style={[viewChilds.views, style1.alignViews]}>

            <Image
                source={require(`../assets/images/${props.img}`)}
                style={{ width: '250px', height: '250px', resizeMode:'center', borderRadius: '100%',backgroundColor:'white', marginBottom: 30, maxWidth: '100%' }} />

        </View>


    );
}

export default Banner;
