import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  inputs: {
    padding: 10,
    borderWidth: 1,
    borderColor: 'green',
    textAling: 'center',
    borderRadius: 10,
    color: 'black',
    textAlign: 'center',
    placeholderTextColor: 'darck-gray',
    fontSize: 25,   
    minWidth: 350,
    height: 60,
    backgroundColor: 'transparent',
    fontFamily: "'PT Sans', sans-serif",
    color:'#505455',
  },
  buttons: {
    padding: 10,
    borderRadius: 10,
    width: 150,
    marginTop: 20,
    backgroundColor: '#097dab',
    minWidth: 200,
    height: 60,    
  },
  textbuttons: {
    color: 'white',
    textAlign: 'center',
    lineHeight: 35,
    fontSize: 25,
     fontFamily: "'PT Sans', sans-serif",   
  },
  texts: {
    fontSize: 22,
    fontWeight: 'bold',  
    marginTop: 15,
    marginBottom: 5,  
    letterSpacing: 2,
    fontFamily: "'PT Sans', sans-serif",
    color:'#505455',
  },
  images: {
    width: '300px', height: '300px', resizeMode: 'center', borderRadius: '100%', backgroundColor: 'white', maxWidth: '100%', backgroundColor:'transparent' 
  }

});

const viewsChilds = StyleSheet.create({
  views: {
    width: '80%',
    marginLeft: '5%',   
  }
});

export { styles, viewsChilds }
