import { styles, viewsChilds } from '../styles/Styles';

const Banner = (props) => {
    return (
        <View style={[viewsChilds.Views, styles.alingViews, { flex: 1, backgroundColor: 'yellow' }]}>
            <Text>{props.title}</Text>
            <Text>{props.subtitle}</Text>
            <Image source={requiere('')}></Image>
            <View style={[viewsChilds.views, { flex: 3, background: 'pink', }]}>
                <Text style={styles.texts}>Cuerpo</Text>
            </View>
        </View>
    )
}

export default Banner;
